import React, { useEffect, useState } from "react";
import { ColorModeContext, useMode } from "./theme";
import EditIcon from "@mui/icons-material/Edit";
import { tokens } from "./theme";
import "./App.css";
import {
  CssBaseline,
  useTheme,
  ThemeProvider,
  Box,
  Button,
  Checkbox,
  Typography,
  colors,
} from "@mui/material";
import { FormControl, TextField } from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import DeleteIcon from "@mui/icons-material/Delete";
import { borderRadius } from "@mui/system";
const App = () => {
  const [theme, colorMode] = useMode();
  const colors = tokens("light");

  const [todos, setTodos] = useState([]);
  const [todo, setTodo] = useState("");
  const [todoEditing, setTodoEditing] = useState(null);
  const [editingText, setEditingText] = useState("");

  useEffect(() => {
    const temp = localStorage.getItem("todos");
    const loadedTodos = JSON.parse(temp);

    if (loadedTodos) {
      setTodos(loadedTodos);
    }
  }, []);

  useEffect(() => {
    const temp = JSON.stringify(todos);
    localStorage.setItem("todos", temp);
  }, [todos]);

  let handleSubmit = (e) => {
    e.preventDefault();
    const newTodo = {
      id: new Date().getTime(),
      text: todo,
      completed: false,
    };

    setTodos([...todos].concat(newTodo));
    setTodo("");
  };

  let deleteTodo = (id) => {
    const updatesTodo = [...todos].filter((todo) => {
      return todo.id !== id;
    });
    setTodos(updatesTodo);
  };

  let toggleComplete = (id) => {
    const updatedTodos = [...todos].map((todo) => {
      if (todo.id === id) {
        todo.completed = !todo.completed;
      }
      return todo;
    });
    setTodos(updatedTodos);
  };

  let editTodo = (id) => {
    const updatedTodos = [...todos].map((todo) => {
      if (todo.id === id) {
        todo.text = editingText;
      }
      return todo;
    });
    setTodos(updatedTodos);
    setTodoEditing(null);
    setEditingText("");
  };

  return (
    <ColorModeContext.Provider value={colorMode}>
      <ThemeProvider theme={theme}>
        <CssBaseline />
        <div className="app">
          <main className="content">
            <Box display="flex" justifyContent="center" alignItems="center">
              <Box padding="40px" mt="50px">
                <Box display="flex" justifyContent="space-between">
                  <TextField
                    id="standard-textarea"
                    placeholder="Enter Task"
                    multiline
                    required
                    label="Task"
                    variant="standard"
                    value={todo}
                    onChange={(e) => {
                      setTodo(e.target.value);
                    }}
                  />
                  <Button
                    onClick={handleSubmit}
                    size="small"
                    variant="outlined"
                    startIcon={<AddIcon />}
                    sx={{ padding: "10px" }}
                  >
                    Add
                  </Button>
                </Box>

                <Box
                  m="30px 0 20px 0"
                  sx={{
                    border: "1px dashed black",
                    borderRadius: "3px",
                    backgroundColor: `${colors.blueAccent[400]}`,
                  }}
                >
                  <Typography
                    textAlign="center"
                    variant="h3"
                    sx={{ color: colors.greenAccent[700] }}
                  >
                    Tasks
                  </Typography>
                </Box>
                {todos.map((todo) => {
                  return (
                    <Box mt="12px" key={todo.id}>
                      <Checkbox defaultChecked sx={{ margin: "0 4px" }} />

                      {todoEditing === todo.id ? (
                        <TextField
                          id="standard-multiline-flexible"
                          label="Edit"
                          multiline
                          maxRows={4}
                          variant="standard"
                          onChange={(e) => setEditingText(e.target.value)}
                          value={editingText}
                        />
                      ) : (
                        <Typography sx={{ overflow: "auto" }} variant="h6">
                          {todo.text}
                        </Typography>
                      )}
                      <Box display="flex">
                        <Button
                          variant="outlined"
                          startIcon={<DeleteIcon />}
                          color="error"
                        >
                          Delete
                        </Button>
                      </Box>
                      {todoEditing === todo.id ? (
                        <Button
                          onClick={() => editTodo(todo.id)}
                          size="small"
                          variant="outlined"
                          startIcon={<EditIcon color="secondary" />}
                          sx={{ padding: "10px" }}
                        >
                          Update
                        </Button>
                      ) : (
                        <Button
                          onClick={() => editTodo(todo.id)}
                          size="small"
                          variant="outlined"
                          startIcon={<EditIcon color="secondary" />}
                          sx={{ padding: "10px" }}
                        >
                          Edit
                        </Button>
                      )}
                    </Box>
                  );
                })}

                {/*


                    {todoEditing === todo.id ? (
                      <button onClick={() => editTodo(todo.id)}>
                        Submit Edits
                      </button>
                    ) : (
                      <button onClick={() => setTodoEditing(todo.id)}>
                        Edit todo
                      </button>
                    )}
                  </div>
                );
              })} */}
              </Box>
            </Box>
          </main>
        </div>
      </ThemeProvider>
    </ColorModeContext.Provider>
  );
};

export default App;
