import React, { useState } from "react";
import "./App.css";
import { CssBaseline } from "@mui/material";

const App = () => {
  const [todos, setTodos] = useState([]);
  const [todo, setTodo] = useState("");

  let handleSubmit = (e) => {
    e.preventDefault();
    const newTodo = {
      id: new Date().getTime(),
      text: todo,
      completed: false,
    };

    setTodos([...todos].concat(newTodo));
    setTodo("");
  };

  let deleteTodo = () => {
    const updatesTodo = [...todos].filter((todo) => {
