    });
  };
  return (
    <>
      {/* <CssBaseline /> */}
      <div className="App">
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            onChange={(e) => {
              setTodo(e.target.value);
            }}
            value={todo}
          />
          <button type="submit">Add Todos</button>
        </form>
        {todos.map((todo) => {
          return (
            <div key={todo.id}>
              <div>{todo.text}</div>
              <button onClick={() => deleteTodo(todo.id)}>Delete</button>
            </div>
          );
        })}
      </div>
    </>
  );
};

export default App;
